mapper.select:
SELECT NVL(MAX(ROWNUM), 0)
FROM board_review
WHERE ref_group=#{ref_group}

mapper.update:
UPDATE board_review
SET review=#{review}, rate=#{rate}
WHERE num=#{num}

mapper.select:

SELECT ROUND(AVG(rate), 2) AS average_rate
FROM board_review
WHERE title=#{title}

SELECT *
FROM board_review
WHERE ref_group = #{ref_group}
AND writer = #{writer}
